dbg-backtrace.content:
function dbg_backtrace(x)
{
 if(is_undef(x))
 {
  const o=new Error("backtrace")

  return dbg_backtrace(o)
 }

 check(is_obj,x)

 const r=arr()
 const s=to_str(x.stack)
 const lines=split(s)

 for(const k in lines)
 {
  const i=to_i(k)
  const v=at(lines,i)

  let s=v

  s=trim(s)
  s=strip_l(s,"at")
  s=trim(s)
  s=replace(s,"@"," ")

  const parts=split(s," ")

  if(!is_pair(parts))
   continue

  const fn=shift(parts)

  if(!is_name(fn))
   continue

  let a=parts

  a=shift(a)
  a=unparen(a)
  a=split(a,":")

  if(lt(a.length,3))
   continue

  const column=pop(a)

  let line=pop(a)

  line=to_uint(line)

  const url=join(a,":")
  const o={fn,url,line}

  push(r,o)
 }

 return r 
}

dbg-callstack.content:
function dbg_callstack(x)
{
 if(is_undef(x))
 {
  const o=new Error("callstack")

  return dbg_callstack(o)
 }

 function get_trace(x)
 {
  check(is_obj,x)

  const fn=x.fn
  const url=x.url
  const line=x.line

  if(has(scripts,url))
  {
   const index=dec(line)

   if(gte(index,0))
   {
    const script=get(scripts,url)

    if(lt(index,script.length))
    {
     const code=at(script,index)

     return {fn,code,line}
    }
   }
  }

  const code=url

  return {fn,code,line}
 }

 const r=arr()

 decompile_sync()

 for(const v of dbg_backtrace(x))
 {
  const o=get_trace(v)

  push(r,o)
 }

 return r 
}

