dbg-origin.content:
function dbg_origin(x)
{
 if(is_undef(x))
 {
  const o=new Error("origin")

  return dbg_origin(o)
 }

 check(is_obj,x)

 decompile_sync()

 const trace=dbg_backtrace(x)

 while(is_many(trace))
 {
  const frame=front(trace)
  const fn=frame.fn
  const url=frame.url
  const filter=arr("throw","stop","check")

  if(contain(filter,fn))
  {
   shift(trace)

   continue
  }

  if(has(scripts,url))
   break

  shift(trace)
 }

 const frame=front(trace)
 const url=frame.url
 const line=frame.line

 if(!has(scripts,url))
  return arr()

 const index=dec(line)

 if(lt(index,0))
  return arr()

 const script=get(scripts,url)

 if(gte(index,script.length))
  return arr()

 return dbg_window(script,line)
}

dbg-window.content:
function dbg_window(x,y,z)
{
 check(is_arr,x)
 check(is_uint,y)

 if(is_undef(z))
  return dbg_window(x,y,7)

 check(is_uint,z)

 const lines=x
 const length=lines.length
 const number=y
 const offset=dec(number)

 function get_window(x)
 {
  check(is_uint,x)

  let r=arr()
  let window=x  
  let half=window

  half=div(half,2)
  half=trunc(half)

  let begin=sub(offset,half)

  if(lt(begin,0))
   begin=0

  let end=add(begin,window)

  if(gt(end,length))
   window=sub(length,begin)

  for(let i=0;i<window;i++)
  {
   const index=add(begin,i)
   const line=inc(index)

   let code=lines

   code=at(code,index)

   if(is_full(code))
   {
    if(same(line,number))
     code=space(">",code)
    else
     code=space(" ",code)

    const o={code,line}

    push(r,o)
   }
  }

  return r
 }

 let r=arr()

 for(let i=0;i<sub(lines.length,z);i++)
 {
  const n=add(i,z)

  r=get_window(i)

  if(gte(r.length,z))
   break
 }

 return r
}

decompile.content:
function decompile(x)
{
 check(is_str,x)

 function deep(x)
 {
  check(is_str,x)

  let r=x

  r=scan(r)
  //r=scan(r,is_token)
  r=reject(r,is_space)
  r=replace(r,"!","not")
  r=reject(r,is_punct)
  r=keywords(r)
  r=join(r," ")

  return r
 }

 function shallow(x)
 {
  check(is_str,x)

  let r=x

  const not=concat(" ","not"," ")

  r=replace(r,"!",not)
  r=delimit(r," ")

  const puncts="()[]{}<>+-*%=,;"

  for(const v of puncts)
  {
   r=delimit(r,v)
  }

  r=delimit(r,"...")
  r=keywords(r)
  r=join(r," ")

  return r
 }

 function keywords(x)
 {
  check(is_arr,x)

  let r=x

  r=replace(r,"let","var")
  r=replace(r,"const","let")
  r=replace(r,"function","fn")
  r=replace(r,"return","ret")
  r=replace(r,"continue","cont")
  r=replace(r,"break","brk")

  return r
 }

 if(contain(x,"\""))
  return deep(x)

 return shallow(x)
}

decompile-stop.content:
function decompile_stop()
{
 if(is_null(decompiler))
  return

 decompiler.return()
 decompiler=null
}

decompile-sync.content:
function decompile_sync()
{
 if(is_null(decompiler))
  return

 while(true)
 {
  const o=decompiler.next()

  if(o.done)
  {
   decompiler=null

   return o.value
  }
 }
}

delimit.content:
function delimit(x,y)
{
 check(is_vec,x)
 check(is_str,y)

 if(is_str(x))
 {
  const a=arr(x)

  return delimit(a,y)
 }

 const r=arr()

 for(const v of x)
 {
  const a=split(v,y)

  for(const v of a)
  {
   if(is_full(v))
    push(r,v)
  }
 }

 return r
}

