function tbl_column_numeric(x,y)
{
 check(is_arr,x)
 check(is_str,y)

 if(is_empty(x))
  return false

 for(const v of x)
 {  
  const s=get(v,y)

  if(!is_num(s))
   return false
 }

 return true
}

tbl-column-width.content:
function tbl_column_width(x,y)
{
 check(is_arr,x)
 check(is_str,y)

 let r=y.length

 for(const v of x)
 {
  let s=v

  s=get(s,y)
  s=to_cell(s)

  r=max(r,s.length)
 }

 return r
}

tbl-columns.content:
function tbl_columns(x)
{
 check(is_arr,x)

 if(is_empty(x))
  return arr()

 const first=front(x)

 return get_keys(first)
}

tbl-render.content:
function tbl_render(x)
{
 check(is_arr,x)

 function is_column_align_r(x)
 {
  check(is_arr,x)

  if(is_empty(x))
   return false

  const first=front(x)
  const n=first.length

  for(const v of x)
  {
   if(different(v.length,n))
    return false
  }

  for(const v of x)
  {
   const c=front(v)

   if(is_space(c))
    return true
  }

  return false
 }

 const tbl=clone(x)
 const labels=tbl_columns(tbl)
 const header=obj()
 const columns=arr()

 for(const v of labels)
 {
  put(header,v,v)
 }

 unshift(tbl,header)

 for(const v of labels)
 {
  if(tbl_column_numeric(x,v))
   tbl_align_r(tbl,v)
  else
   tbl_align_l(tbl,v)

  const column=tbl_column(tbl,v)

  if(is_column_align_r(column))
  {
   const title1=shift(column)
   const n=title1.length
   const title2=trim(title1)
   const title3=pad_l(title2," ",n)

   unshift(column,title3)
  }

  push(columns,column)
 }

 const lines=arr()
 let width=0

 for(const k in front(columns))
 {
  const i=to_i(k)
  const row=arr()
  const i1=i

  for(const k in labels)
  {
   const i=to_i(k)
   const column=at(columns,i)
   const cell=at(column,i1)

   push(row,cell)
  }

  let line=row

  line=join(line," ")
  line=trim_r(line)

  width=max(width,line.length)

  push(lines,line)
 }

 const separator=repeat("-",width)

 unshift(lines,separator)
 insert(lines,1,separator)
 push(lines,separator)

 return join(lines)
}

time-get.content:
function time_get()
{
 let r=Date.now()

 r=div(r,1000)

 return r
}

time-now.content:
function time_now()
{
 let r=time_get()

 r=sub(r,startup)

 return r
}

timer-timeout.content:
function timer_timeout(x,y,...z)
{
 check(is_fn,x)
 check(is_num,y) 
 check(gte,y,0)
 check(alive)

 const delay=mul(y,1000)

 return setTimeout(x,delay,...z)
}

to-bsize.content:
function to_bsize(x)
{
 check(is_uint,x)
 check(gte,x,0)

 const kb=1024
 const mb=mul(1024,kb)
 const gb=mul(1024,mb)
 const tb=mul(1024,gb)

 if(lt(x,kb))
 {
  const s=to_fixed(x)

  return concat(s,"b")
 }

 if(lt(x,mb))
 {
  const n=div(x,kb)
  const s=to_fixed(n)

  return concat(s,"Kb")
 }

 if(lt(x,gb))
 {
  const n=div(x,mb)
  const s=to_fixed(n)

  return concat(s,"Mb")
 }

 if(lt(x,tb))
 {
  const n=div(x,gb)
  const s=to_fixed(n)

  return concat(s,"Gb")
 }

 const n=div(x,tb)
 const s=to_fixed(n)

 return concat(s,"Tb")
}

to-cell.content:
function to_cell(x)
{
 check(is_def,x)

 let r=x

 r=to_str(r)
 r=split(r)
 r=front(r)
 r=trim_r(r)

 return r
}

to-fixed.content:
function to_fixed(x,y)
{
 check(is_num,x)

 if(is_undef(y))
  return to_fixed(x,2)

 check(is_uint,y)

 let s=y

 s=x.toFixed(y)

 let a=s

 a=split(s,".")

 if(is_single(a))
  return s

 const integer=front(a)
 const float=back(a)

 a=explode(float)

 while(is_full(a))
 {
  const c=back(a)

  if(different(c,"0"))
   break

  pop(a)
 }

 if(is_empty(a))
  return integer

 s=implode(a)

 return concat(integer,".",s) 
}

to-int.content:
function to_int(x)
{
 check(is_str,x)

 const r=Number.parseInt(x)

 check(is_num,r)

 return r
}

to-str.content:
function to_str(x)
{
 check(is_def,x)

 if(is_null(x))
  return "null"

 return x.toString()
}

to-uint.content:
function to_uint(x)
{
 check(is_str,x)

 const r=to_int(x)

 check(gte,r,0)

 return r 
}

tokenize.content:
function tokenize(x)
{
 check(is_vec,x)

 let r=x

 if(is_str(r))
  r=arr(r)

 r=delimit(r," ")
 r=delimit(r,"\r")
 r=delimit(r,"\n")
 r=delimit(r,"\t")

 return r
}

trim.content:
function trim(x)
{
 check(is_str,x)

 let r=x

 r=trim_l(r)
 r=trim_r(r)

 return r
}

txt-left.content:
function txt_left(x,y)
{
 check(is_str,x)
 check(is_uint,y)

 const a=arr()

 for(const v of split(x))
 {
  const s=head(v,y)

  push(a,s)
 }

 return join(a)
}

uncomment.content:
function uncomment(x)
{
 check(is_str,x)

 const output=arr()

 for(const v of split(x))
 {
  const s1=trim_r(v)

  if(is_empty(s1))
   continue

  const s2=trim_l(s1)
  const indent=sub(s1.length,s2.length)   
  const tokens=recognize(s2)

  if(is_empty(tokens))
   continue

  const s3=collate(tokens)
  const s4=repeat(" ",indent)
  const s5=concat(s4,s3)

  push(output,s5)
 } 

 return join(output)
}

unparen.content:
function unparen(x)
{
 check(is_str,x)

 if(is_parened(x))
 {
  const n=sub(x.length,2)

  return slice(x,1,n)
 }

 return x
}

untrivia.content:
function untrivia(x)
{
 check(is_str,x)

 const output=arr()

 for(const v of split(x))
 {
  const s1=trim_r(v)

  if(is_empty(s1))
   continue

  const tokens=recognize(s1)

  if(is_empty(tokens))
   continue

  const s2=join(tokens," ")

  push(output,s2)
 } 

 return join(output)
}

unwrap.content:
function unwrap(x)
{
 check(is_str,x)

 if(is_lit(x))
  return from_json(x)

 if(is_access(x))
  return split(x,".")

 if(is_tuple(x))
  return split(x,":")

 stop()
}
